import React, { useState, useEffect } from "react";
import { Card } from "../Components/Card/card";
import { Form } from "../Components/Form/form";

export const ContractorCalculator = () => {
  const [todo, setTodo] = useState([]);
  const [addTodo, setAddTodo] = useState("");
  const [secondDim, setSecondDim] = useState("");
  useEffect(() => {
    fetch("/api")
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
      })
      .then((data) => setTodo(data));
  }, []);

  const handleFormChange = (firstInputValue, secondInputValue) => {
    setAddTodo(firstInputValue);
    setSecondDim(secondInputValue);
  };

  const handleFormSubmit = () => {
    fetch("/api/create", {
      method: "POST",
      body: JSON.stringify({
        content: addTodo,
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    })
      .then((response) => response.json())
      .then((message) => {
        setAddTodo("");
        setSecondDim("");
        getLatestTodos();
      });
  };

  const getLatestTodos = () => {
    fetch("/api")
      .then((response) => {
        return response.json();
      })
      .then((data) => setTodo(data));
  };

  return (
    <>
      <h1>Contracting Calculator</h1>
      <Form
        userInput={addTodo}
        secondDimension={secondDim}
        onFormChange={handleFormChange}
        onFormSubmit={handleFormSubmit}
      />
      <Card listOfTodos={todo} />
    </>
  );
};
