import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

// The userInput (aka: 'content') and the id are passed in from the web page... these are used in updateTodo to call the server
export const Update = ({ userInput, id, onFormChange, onFormSubmit }) => {
  const navigate = useNavigate();

  // This function speaks directly to the Flask Route for updating... May need to change it to /api/update/${id} to distinguish properly
  const updateTodo = () => {
    fetch(`/api/${id}`, {
      method: "POST",
      body: JSON.stringify({
        // Passing in the id value and the content for the POST operation
        id: id,
        content: userInput,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        navigate("/");
      });
  };

  const handleChange = (event) => {
    onFormChange(event.target.value);
  };

  return (
    <>
      <form onSubmit={updateTodo}>
        <input
          type="text"
          required
          value={userInput}
          onChange={handleChange}
        ></input>
        <input type="submit" value="Update"></input>
      </form>
    </>
  );
};
