import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

// The userInput (aka: 'content') and the id are passed in from the web page... these are used in updateTodo to call the server
export const Update = ({ userInput, onFormChange, onFormSubmit, id }) => {
  const navigate = useNavigate();

  // This function speaks directly to the Flask Route for updating... May need to change it to /api/update/${id} to distinguish properly
  const handleChange = (event) => {
    onFormChange(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onFormSubmit();
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          required
          value={userInput}
          onChange={handleChange}
          id={id}
        ></input>
        <input type="submit" value="Update"></input>
      </form>
    </>
  );
};
