export const ContractorCalculator = () => {
  const [calculation, setCalculation] = useState([]);
  const [state, setState] = useState({
    firstDimension: "",
    secondDimension: "",
  });
  useEffect(() => {
    fetch("/api")
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
      })
      .then((data) => setCalculation(data));
  }, []);

  const handleFormChange = (targetVal) => {
    state.setState({ [targetVal.name]: targetVal.value });
    //setState({ [state.targetVal.name]: targetVal.value });
    console.log(state.firstDimension, state.secondDimension);
  };

  const handleFormSubmit = () => {
